import { HttpHeaders, HttpParams } from "@angular/common/http";
import { environment } from "../../environments/environment.prod";
import { formatDate } from '@angular/common';
import { Energy } from '../Entity/energy';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "@angular/common";
export class PlantService {
    constructor(httpClient, datePipe) {
        this.httpClient = httpClient;
        this.datePipe = datePipe;
        if (localStorage.getItem("currentUser") != null) {
            this._currentUser = JSON.parse(atob(localStorage.getItem("currentUser")));
        }
        this._energies = new Array();
        this._plants = new Array();
    }
    async initialize() {
        (await this.getPlants()).forEach(async (next) => {
            this._plants = next;
            this._plants.forEach(p => {
                this._energies.push(new Energy());
                // console.log(p)
            });
            for (let i = 0; i < this._energies.length; i++) {
                (await this.fetchPlantDatasBetweenDates(this._plants[i], '2022-01-01', '2022-12-31')).forEach(ps => {
                    this._energies[i].status = ps;
                    this._energies[i].plant = this._plants[i];
                    ps.forEach(s => {
                        this._energies[i].thisYearEnergy[this.datePipe.transform(s.dateTime, 'MM') - 1] += s.current * s.voltage * s.powerFactor;
                        this._energies[i].thisYearTotalEnergy += s.current * s.voltage * s.powerFactor;
                        if (this.datePipe.transform(s.dateTime, 'yyyy-MM-dd') == this.datePipe.transform(new Date(), 'yyyy-MM-dd')) {
                            this._energies[i].todayHours.push(this.datePipe.transform(s.dateTime, 'HH:mm:ss'));
                            this._energies[i].todayCurrent.push(s.current);
                            this._energies[i].todayVoltage.push(s.voltage);
                            this._energies[i].todayPowerFactor.push(s.powerFactor);
                            this._energies[i].todayPower.push(s.current * s.voltage * s.powerFactor);
                            this._energies[i].todayEnergy += s.current * s.voltage * s.powerFactor;
                        }
                    });
                });
                this._dailyEnergy.push(this._energies[i].todayEnergy);
                this._yearlyEnergy.push(this._energies[i].thisYearTotalEnergy);
            }
            console.log(this._energies);
        });
    }
    // getPlant(id) {
    //   const headers = new HttpHeaders(this._currentUser ? {
    //     Authorization: 'Basic ' + btoa(this._currentUser.username + ':' + this._currentUser.password)
    //   } : {});
    //   return this.httpClient.get<Plant>(environment.apiPlantUrl + "/" + id, { headers: headers });
    // }
    async getPlants() {
        return this.httpClient.get(environment.apiPlantUrl, {});
    }
    getSelectedPlant() {
        return this._selectedPlant;
    }
    setSelectedPlant(plant) {
        localStorage.setItem("selectedPlant", JSON.stringify(plant));
        this._selectedPlant = plant;
    }
    getEnergies() {
        return this._energies;
    }
    async fetchPlantDatasBetweenDates(plant, date1, date2) {
        const headers = new HttpHeaders(this._currentUser ? {
            Authorization: 'Basic ' + btoa(this._currentUser.username + ':' + this._currentUser.password)
        } : {});
        const parameters = {
            "date1": date1,
            "date2": date2
        };
        let queryParams = new HttpParams({ fromObject: parameters });
        return await this.httpClient.get(plant.link + '/between', {
            headers: headers,
            params: queryParams
        });
    }
    async fetchPlantDatasToday(plant) {
        const headers = new HttpHeaders(this._currentUser ? {
            Authorization: 'Basic ' + btoa(this._currentUser.username + ':' + this._currentUser.password)
        } : {});
        const parameters = {
            "date": formatDate(new Date(), 'yyyy-MM-dd', 'en')
            // "date": '2022-04-18'
        };
        let queryParams = new HttpParams({ fromObject: parameters });
        return this.httpClient.get(plant.link + '/from', {
            headers: headers,
            params: queryParams
        });
    }
    updatePlantByName(name, plant) {
        const headers = new HttpHeaders(this._currentUser ? {
            Authorization: 'Basic ' + btoa(this._currentUser.username + ':' + this._currentUser.password)
        } : {});
        return this.httpClient.put(environment.apiPlantUrl + '/' + name, plant);
    }
}
