import { Chart, BarElement, BarController, CategoryScale, Decimation, Filler, Legend, Title, Tooltip, LinearScale, LineElement, LineController, PointElement } from 'chart.js';
import { Energy } from 'src/app/Entity/energy';
import * as i0 from "@angular/core";
import * as i1 from "../../Service/plant.service";
import * as i2 from "src/app/Service/energy.service";
import * as i3 from "@angular/forms";
import * as i4 from "@angular/router";
export class PlantSelectedComponent {
    constructor(plantService, energyService) {
        this.plantService = plantService;
        this.energyService = energyService;
        Chart.register(BarElement, BarController, CategoryScale, Decimation, Filler, Legend, Title, Tooltip, LinearScale, LineElement, LineController, PointElement);
    }
    async ngOnInit() {
        this._energy = new Energy();
        this._selectedPlantCopy = JSON.parse(JSON.stringify(this.plantService.getSelectedPlant())); //DEEP COPY
        this._energy = (await this.energyService.getEnergies()).filter(e => e.plant.name == this._selectedPlantCopy.name)[0];
        console.log(this._energy);
        const dataCurrentAndVoltage = {
            labels: this._energy.todayHours,
            datasets: [
                {
                    label: 'Current',
                    data: this._energy.todayCurrent,
                    borderColor: 'rgb(83, 13, 192)',
                    backgroundColor: 'rgb(83, 13, 192)',
                    tension: 0.1,
                    yAxisID: 'y',
                },
                {
                    label: 'Voltage',
                    data: this._energy.todayVoltage,
                    borderColor: 'rgb(83, 192, 192)',
                    backgroundColor: 'rgb(83, 192, 192)',
                    tension: 0.1,
                    yAxisID: 'y1',
                }
            ],
        };
        this._myChartCurrentAndVoltage = new Chart("myChartCurrentAndVoltage", {
            type: 'line',
            data: dataCurrentAndVoltage,
            options: {
                scales: {
                    y: {
                        type: 'linear',
                        display: true,
                        position: 'left',
                        ticks: {
                            // Include a dollar sign in the ticks
                            callback: function (value, index, ticks) {
                                return value + ' A';
                            }
                        }
                    },
                    y1: {
                        type: 'linear',
                        display: true,
                        position: 'right',
                        grid: {
                            drawOnChartArea: false, // only want the grid lines for one axis to show up
                        },
                        ticks: {
                            // Include a dollar sign in the ticks
                            callback: function (value, index, ticks) {
                                return value + ' V';
                            }
                        }
                    }
                },
                plugins: {
                    datalabels: {
                        formatter: function (value, context) {
                            return '';
                        }
                    }
                }
            }
        });
        this._myChartPowerFactor = new Chart("myChartPowerFactor", {
            type: 'bar',
            data: {
                labels: this._energy.todayHours,
                datasets: [{
                        label: 'Power factor',
                        data: this._energy.todayPowerFactor,
                        backgroundColor: [
                            'rgba(255, 99, 132, 0.55)',
                        ],
                        borderColor: [
                            'rgba(255, 99, 132, 1)',
                        ],
                        borderWidth: 1
                    }]
            },
            options: {
                elements: {
                    bar: {
                        borderWidth: 2,
                    }
                },
                scales: {
                    y: {
                        beginAtZero: false,
                        ticks: {
                            // Include a dollar sign in the ticks
                            callback: function (value, index, ticks) {
                                return value * 100 + '%';
                            }
                        }
                    }
                },
                plugins: {
                    datalabels: {
                        formatter: function (value, context) {
                            return '';
                        }
                    }
                }
            }
        });
        this._todayPowerChart = new Chart("myAreaChartPower", {
            type: 'line',
            data: {
                labels: this._energy.todayHours,
                datasets: [{
                        label: "Power",
                        tension: 0.5,
                        backgroundColor: "rgba(2,117,216,0.2)",
                        borderColor: "rgba(2,117,216,1)",
                        pointRadius: 5,
                        pointBackgroundColor: "rgba(2,117,216,1)",
                        pointBorderColor: "rgba(255,255,255,0.8)",
                        pointHoverRadius: 5,
                        pointHoverBackgroundColor: "rgba(2,117,216,1)",
                        pointHitRadius: 50,
                        pointBorderWidth: 2,
                        data: this._energy.todayPower,
                        fill: {
                            target: 'origin',
                            above: 'rgb(2,117,216,0.2)',
                            below: 'rgb(2,117,216,0.2)' // And blue below the origin
                        }
                    }],
            },
            options: {
                scales: {
                    x: {},
                    y: {
                        beginAtZero: true,
                        ticks: {
                            // Include a dollar sign in the ticks
                            callback: function (value, index, ticks) {
                                return value / 1000 + ' kW';
                            }
                        }
                    },
                },
                plugins: {
                    datalabels: {
                        formatter: function (value, context) {
                            return '';
                        }
                    }
                }
            }
        });
        this._thisYearEnergyChart = new Chart("myChartYearContribution", {
            type: 'bar',
            data: {
                labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sept', 'Oct', 'Nov', 'Dec'],
                datasets: [{
                        label: "Energy Contribution",
                        backgroundColor: "rgba(0,198,152,0.5)",
                        borderColor: "rgb(0,198,152,1)",
                        data: this._energy.thisYearEnergy,
                    }],
            },
            options: {
                indexAxis: 'y',
                elements: {
                    bar: {
                        borderWidth: 2,
                    }
                },
                responsive: true,
                scales: {
                    x: {
                        ticks: {
                            // Include a dollar sign in the ticks
                            callback: function (value, index, ticks) {
                                return value + ' MWh';
                            }
                        }
                    },
                    y: {},
                },
                plugins: {
                    datalabels: {
                        formatter: function (value, context) {
                            return '';
                        }
                    }
                }
            }
        });
    }
    getSelectedPlant() {
        return this.plantService.getSelectedPlant();
    }
    getEnergy() {
        return this._energy;
    }
    removeSelectedPlant() {
        this.plantService.setSelectedPlant(null);
        localStorage.removeItem("selectedPlant");
    }
    getThisYear() {
        return new Date().toISOString().substring(0, 4);
    }
    updatePlantByName(plant) {
        this.plantService.updatePlantByName(this.plantService.getSelectedPlant().name, plant).subscribe(v => {
            console.log(v);
        }, err => {
            console.log(err);
        }, () => {
            console.log('Gata');
        });
    }
}
