export class PlantService {
    constructor(httpClient, datePipe) {
        this.httpClient = httpClient;
        this.datePipe = datePipe;
        if (localStorage.getItem("currentUser") != null) {
            this._currentUser = JSON.parse(atob(localStorage.getItem("currentUser")));
        }
        this._energies = new Array();
        this._plants = new Array();
        this.getPlants().forEach(next => {
            this._plants = next;
            this._plants.forEach(p => {
                this._energies.push(new Energy());
                // console.log(p)
            });
            for (let i = 0; i < this._energies.length; i++) {
                this.fetchPlantDatasBetweenDates(this._plants[i], '2022-01-01', '2022-12-31').forEach(ps => {
                    this._energies[i].status = ps;
                    this._energies[i].plant = this._plants[i];
                    ps.forEach(s => {
                        this._energies[i].thisYearEnergy[this.datePipe.transform(s.dateTime, 'MM') - 1] += s.current * s.voltage * s.powerFactor;
                        this._energies[i].thisYearTotalEnergy += s.current * s.voltage * s.powerFactor;
                        if (this.datePipe.transform(s.dateTime, 'yyyy-MM-dd') == this.datePipe.transform(new Date(), 'yyyy-MM-dd')) {
                            this._energies[i].todayHours.push(this.datePipe.transform(s.dateTime, 'HH:mm:ss'));
                            this._energies[i].todayCurrent.push(s.current);
                            this._energies[i].todayVoltage.push(s.voltage);
                            this._energies[i].todayPowerFactor.push(s.powerFactor);
                            this._energies[i].todayPower.push(s.current * s.voltage * s.powerFactor);
                            this._energies[i].todayEnergy += s.current * s.voltage * s.powerFactor;
                        }
                    });
                });
            }
            // console.log(this._energies)
        });
    }
    // getPlant(id) {
