    //   return this.httpClient.get<Plant>(environment.apiPlantUrl + "/" + id, { headers: headers });
    // }
    getPlants() {
        return this.httpClient.get(environment.apiPlantUrl, {});
    }
    getSelectedPlant() {
        return this._selectedPlant;
    }
    setSelectedPlant(plant) {
        localStorage.setItem("selectedPlant", JSON.stringify(plant));
        this._selectedPlant = plant;
    }
    getEnergies() {
        return this._energies;
    }
    fetchPlantDatasBetweenDates(plant, date1, date2) {
        const headers = new HttpHeaders(this._currentUser ? {
            Authorization: 'Basic ' + btoa(this._currentUser.username + ':' + this._currentUser.password)
        } : {});
        const parameters = {
            "date1": date1,
            "date2": date2
        };
        let queryParams = new HttpParams({ fromObject: parameters });
        return this.httpClient.get(plant.link + '/between', {
            headers: headers,
            params: queryParams
        });
    }
    async fetchPlantDatasToday(plant) {
        const headers = new HttpHeaders(this._currentUser ? {
            Authorization: 'Basic ' + btoa(this._currentUser.username + ':' + this._currentUser.password)
        } : {});
        const parameters = {
            "date": formatDate(new Date(), 'yyyy-MM-dd', 'en')
            // "date": '2022-04-18'
        };
        let queryParams = new HttpParams({ fromObject: parameters });
        return this.httpClient.get(plant.link + '/from', {
            headers: headers,
            params: queryParams
        });
    }
    updatePlantByName(name, plant) {
        const headers = new HttpHeaders(this._currentUser ? {
            Authorization: 'Basic ' + btoa(this._currentUser.username + ':' + this._currentUser.password)
        } : {});
        return this.httpClient.put(environment.apiPlantUrl + '/' + name, plant);
    }
}
