import { BarController, BarElement, CategoryScale, Chart, Decimation, Filler, Legend, LinearScale, LineController, LineElement, PointElement, Title, Tooltip, PieController, ArcElement } from 'chart.js';
import ChartDataLabels from 'chartjs-plugin-datalabels';
import ChartModule from 'chartjs-plugin-datalabels';
import * as i0 from "@angular/core";
import * as i1 from "src/app/Service/plant.service";
export class GeneralStatisticsComponent {
    constructor(plantService) {
        this.plantService = plantService;
        this._dailyEnergy = new Array();
        this._yearlyEnergy = new Array();
        this._contribution = new Array();
        Chart.register(BarElement, BarController, CategoryScale, Decimation, Filler, Legend, Title, Tooltip, LinearScale, LineElement, LineController, PointElement, PieController, ArcElement, ChartDataLabels, ChartModule);
    }
    async ngOnInit() {
        this._energies = await this.plantService.getEnergies();
        console.log(this._energies);
    }
    ngAfterViewInit() {
        this._dailyPie = new Chart("myDaily", {
            type: 'pie',
            data: {
                labels: this._contribution,
                datasets: [{
                        label: "Energy Contribution",
                        data: this._dailyEnergy,
                        backgroundColor: [
                            'rgb(255, 159, 64)',
                            'rgb(255, 205, 86)',
                            'rgb(0, 163, 51)',
                            'rgb(54, 162, 235)',
                            'rgb(153, 102, 255)',
                            'rgb(201, 203, 207)',
                            'rgb(0,0,255)'
                        ],
                    }],
            },
            plugins: [ChartDataLabels, ChartModule],
            options: {
                plugins: {
                    title: {
                        display: true,
                        text: 'Daily contribution in kWh'
                    },
                    datalabels: {
                        color: 'rgb(255,255,255)',
                        font: {
                            size: 13
                        },
                        formatter: function (value, context) {
                            return value.toPrecision(7) + ' kWh';
                        }
                    },
                },
            },
        });
        this._yearlyPie = new Chart("myYearly", {
            type: 'doughnut',
            plugins: [ChartDataLabels, ChartModule],
            data: {
                labels: this._contribution,
                datasets: [{
                        label: "Energy Contribution",
                        data: this._yearlyEnergy,
                        backgroundColor: [
                            'rgb(0,0,255)',
                            'rgb(255, 159, 64)',
                            'rgb(255, 205, 86)',
                            'rgb(153, 102, 255)',
                            'rgb(0, 163, 51)',
                            'rgb(201, 203, 207)',
                            'rgb(54, 162, 235)',
                        ],
                    }],
            },
            options: {
                plugins: {
                    title: {
                        display: true,
                        text: 'Yearly contribution in MWh'
                    },
                    datalabels: {
                        color: 'rgb(255,255,255)',
                        font: {
                            size: 13
                        },
                        formatter: function (value, context) {
                            return value.toPrecision(6) + ' MWh';
                        }
                    }
                },
            },
        });
    }
    getDailyEnergy() {
        let energy = 0;
        this._dailyEnergy.forEach(e => energy += e);
        return energy;
    }
    getYearlyEnergy() {
        let energy = 0;
        this._yearlyEnergy.forEach(e => energy += e);
        return energy / 1000;
    }
}
